#include <windows.h>size_t randomize(size_t seed) { 	return seed * 1103515245UL + 12345UL; }void RandInit(){#ifdef _WIN32 	//用QueryPerformanceCounter()来计时  微秒  	LARGE_INTEGER  large_interger;	QueryPerformanceCounter(&large_interger);	DWORD pID = GetCurrentProcessId();	srand(large_interger.QuadPart+randomize(pID));#else	struct timespec time_now;	clock_gettime(CLOCK_REALTIME, &time_now);	pid_t pID = getpid();	srand(time_now.tv_nsec + randomize(pID));#endif}